query GetItemDetails($spaceId: String!, $itemId: String!) {
  game(spaceId: $spaceId) {
    marketableItem(itemId: $itemId) {
      marketData {
        ...MarketDataFragment
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment MarketDataFragment on MarketableItemMarketData {
  sellStats {
    lowestPrice
    activeCount
    __typename
  }
  buyStats {
    highestPrice
    activeCount
    __typename
  }
  lastSoldAt {
    price
    performedAt
    __typename
  }
  __typename
}
