query:
query GetItemDetails($spaceId: String!, $itemId: String!, $tradeId: String!, $fetchTrade: Boolean!) {
  game(spaceId: $spaceId) {
    id
    marketableItem(itemId: $itemId) {
      id
      item {
        ...SecondaryStoreItemFragment
        ...SecondaryStoreItemOwnershipFragment
        __typename
      }
      marketData {
        ...MarketDataFragment
        __typename
      }
      paymentLimitations {
        id
        paymentItemId
        minPrice
        maxPrice
        __typename
      }
      __typename
    }
    viewer {
      meta {
        id
        trades(filterBy: {states: [Created], itemIds: [$itemId]}) {
          nodes {
            ...TradeFragment
            __typename
          }
          __typename
        }
        trade(tradeId: $tradeId) @include(if: $fetchTrade) {
          ...TradeFragment
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment SecondaryStoreItemFragment on SecondaryStoreItem {
  id
  assetUrl
  itemId
  name
  tags
  type
  __typename
}

fragment SecondaryStoreItemOwnershipFragment on SecondaryStoreItem {
  id
  viewer {
    meta {
      id
      isOwned
      quantity
      __typename
    }
    __typename
  }
  __typename
}

fragment MarketDataFragment on MarketableItemMarketData {
  id
  sellStats {
    id
    paymentItemId
    lowestPrice
    highestPrice
    activeCount
    __typename
  }
  buyStats {
    id
    paymentItemId
    lowestPrice
    highestPrice
    activeCount
    __typename
  }
  lastSoldAt {
    id
    paymentItemId
    price
    performedAt
    __typename
  }
  __typename
}

fragment TradeFragment on Trade {
  id
  tradeId
  state
  category
  createdAt
  expiresAt
  lastModifiedAt
  failures
  tradeItems {
    id
    item {
      ...SecondaryStoreItemFragment
      ...SecondaryStoreItemOwnershipFragment
      __typename
    }
    __typename
  }
  payment {
    id
    item {
      ...SecondaryStoreItemQuantityFragment
      __typename
    }
    price
    transactionFee
    __typename
  }
  paymentOptions {
    id
    item {
      ...SecondaryStoreItemQuantityFragment
      __typename
    }
    price
    transactionFee
    __typename
  }
  paymentProposal {
    id
    item {
      ...SecondaryStoreItemQuantityFragment
      __typename
    }
    price
    __typename
  }
  viewer {
    meta {
      id
      tradesLimitations {
        ...TradesLimitationsFragment
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}

fragment SecondaryStoreItemQuantityFragment on SecondaryStoreItem {
  id
  viewer {
    meta {
      id
      quantity
      __typename
    }
    __typename
  }
  __typename
}

fragment TradesLimitationsFragment on UserGameTradesLimitations {
  id
  buy {
    id
    resolvedTransactionCount
    resolvedTransactionPeriodInMinutes
    activeTransactionCount
    __typename
  }
  sell {
    id
    resolvedTransactionCount
    resolvedTransactionPeriodInMinutes
    activeTransactionCount
    resaleLocks {
      id
      itemId
      expiresAt
      __typename
    }
    __typename
  }
  __typename
}
