query GetTransactions($spaceId: String!, $limit: Int!, $offset: Int) {
  game(spaceId: $spaceId) {
    id
    viewer {
      meta {
        id
        trades(
          limit: $limit
          offset: $offset
          filterBy: {states: [Created, Succeeded, Failed]}
          sortBy: {field: LAST_MODIFIED_AT}
        ) {
          nodes {
            ...TradeFragment
            __typename
          }
          totalCount
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment TradeFragment on Trade {
  id
  tradeId
  state
  category
  createdAt
  lastModifiedAt
  tradeItems {
    item {
      itemId
      name
      tags
      type
      assetUrl
      __typename
    }
    __typename
  }
  payment {
    price
    __typename
  }
  paymentProposal {
    price
    __typename
  }
  __typename
}
